# -*- coding: utf-8 -*-# 我的Anki插件# 作者：混沌(youliang@chaoskey.com), http://blog.chaoskey.com# 评分按钮配色from aqt import mwfrom aqt.reviewer import Reviewerfrom aqt.qt import *from comm import onLoad,onSwitcholdAnswerButtonList = Reviewer._answerButtonListdef answerButtonList(self):    l = ((1, "<font color='red'>" + _("Again") + "</font>"),)    cnt = self.mw.col.sched.answerButtons(self.card)    if cnt == 2:        return l + ((2, "<font color='green'>" + _("Good") + "</font>"),)    elif cnt == 3:        return l + ((2, "<font color='green'>" + _("Good") + "</font>"), (3, _("Easy")))    else:        return l + ((2, _("Hard")), (3, "<font color='green'>" + _("Good") + "</font>"), (4, _("Easy")))colorAction=QAction(u"评分按钮配色(暂时不可配置)",mw)colorAction.setEnabled(False)def load():    Reviewer._answerButtonList = answerButtonListdef unload():    Reviewer._answerButtonList = oldAnswerButtonList# 本模块的信息ModuleInfo = {'name': u'评分按钮配色'}plugName=os.path.basename(__file__).replace(".py", "")# 添加功能开关action=QAction(ModuleInfo["name"], mw)action.setCheckable(True)action.connect(action, SIGNAL("triggered()"),lambda:onSwitch(load,unload,action,plugName))ModuleInfo.update({"action":action})# 功能加载onLoad(load,action,plugName)