# -*- coding: utf-8 -*-# 我的Anki插件# 作者：混沌(youliang@chaoskey.com), http://blog.chaoskey.com# 清理非本版本的应有文件(防止老版本过期文件影响程序的正常运行)import oscurrPlugs=["UpdateMonitor",	"FullScreen",	"StyleChange",	"DICT",	"ContextSearch",	"ButtonColor",	"ReaderMode",	"Clear",	"TTS",	"VideoSupport",	"Warner",	"__init__",	"MediaSubdirectorySupport"]path=os.path.dirname(__file__)files = os.listdir(path)for f in files:        name = f.split('.')        if len(name) > 1 and name[1] == 'py' and (name[0] not in currPlugs):        	os.unlink(path+"/"+f)# 本模块的信息ModuleInfo = {'name': u'删除过期文件'}